import React from 'react';
import logo from './logo.svg';
import './App.css';

import USAMap from "react-usa-map";


function App() {
    const [states, setStates] = React.useState({})

    const [electoralVotes, setElectoralVotes] = React.useState({
        democrats: 0,
        republicans: 0,
    })

  const mapHandler = (event) => {
    const stateId = event.target.dataset.name
    let newStates = JSON.parse(JSON.stringify(states))
    if (stateId in newStates) {
        if(newStates[stateId].fill === 'navy') newStates[stateId].fill = 'red'
        else newStates[stateId].fill = 'navy'
    } else {
        newStates[stateId] = {'fill': 'navy'}
    }

    setStates(newStates)
  };
  return (
    <div className="App">
      <USAMap customize={states} onClick={mapHandler} />
    </div>
  );
}

export default App;
